import styles from "./IngridientDetails.module.css";
import ReactDOM from "react-dom";
import IngredientDetailNutrition from "./IngridientDetailNutrition/IngridientDetailNutrition";
import PropTypes from "prop-types";

const modalRoot = document.getElementById("modal");

export default function IngredientDetails({
  ingredient,
  isModalOpened,
  toggleModal,
}) {
  if (isModalOpened) {
    return ReactDOM.createPortal(
      <section className={styles.ingridient_details_modal}>
        <div className={styles.ingridient__modal_header}>
          <h2 className="text text_type_main-large">Детали ингрeдиента</h2>
          <button
            id="button_modal_close"
            onClick={() => toggleModal()}
            className={styles.ingridient__modal_close}
          ></button>
        </div>
        <img src={ingredient.image} style={styles.ingridient__image} />
        <p
          className="text text_type_main-medium mt-4"
          src={ingredient.image}
          style={{ alignSelf: "center" }}
        >
          {ingredient.name}
        </p>
        <IngridientDetailNutrition
          key={ingredient._id}
          ingredient={ingredient}
        />
      </section>,
      modalRoot
    );
  }
}

IngridientDetails.PropTypes = {
  ingridient: PropTypes.object,
  isModalOpened: PropTypes.bool,
  toggleModal: PropTypes.func,
};
