import styles from "./BurgerIngridientsBlock.module.css";
import IngridientCard from "../BurgerIngridientCard/IngridientCard";
import PropTypes from "prop-types";

export default function IngridientsBlock({
  ingridientBlockName,
  ingridientsArray,
}) {
  const ingridientGroupArray = ingridientsArray.filter(
    (element) => element.type === ingridientBlockName.value
  );

  return (
    <section className="mt-10">
      <li className="text text_type_main-medium" id={ingridientBlockName.value}>
        {ingridientBlockName.name}
      </li>
      <div className={styles.ingridient_block_grid}>
        {ingridientGroupArray.map((groupIngridient) => (
          <IngridientCard
            key={groupIngridient.name}
            ingridient={groupIngridient}
          ></IngridientCard>
        ))}
      </div>
    </section>
  );
}

IngridientsBlock.PropTypes = {
  ingridientBlockName: PropTypes.object,
  ingridientsArray: PropTypes.array,
};
