import styles from "./BurgerIngridientsBlock.module.css";
import IngredientCard from "../BurgerIngridientCard/IngridientCard";
import PropTypes from "prop-types";

export default function IngredientsBlock({
  ingredientBlockName,
  ingredientsArray,
}) {
  const ingredientGroupArray = ingredientsArray.filter(
    (element) => element.type === ingredientBlockName.value
  );

  return (
    <section className="mt-10">
      <li className="text text_type_main-medium" id={ingredientBlockName.value}>
        {ingredientBlockName.name}
      </li>
      <div className={styles.ingridient_block_grid}>
        {ingredientGroupArray.map((groupIngredient) => (
          <IngredientCard
            key={groupIngredient.name}
            ingredient={groupIngredient}
          ></IngredientCard>
        ))}
      </div>
    </section>
  );
}

IngredientsBlock.PropTypes = {
  ingredientBlockName: PropTypes.object,
  ingredientsArray: PropTypes.array,
};
