import styles from "./IngridientDetailNutrition.module.css";

export default function IngridientDetailNutrition({ ingridient }) {
  const ingridientCompositionArray = [
    { name: "Калории,ккал", value: ingridient.colories },
    { name: "Белки,г", value: ingridient.proteins },
    { name: "Жиры,г", value: ingridient.fat },
    { name: "Углеводы,г", value: ingridient.carbohydrates },
  ];
  return (
    <div className={styles.ingridient__nutrition}>
      {ingridientCompositionArray.map((type) => (
        <div className={styles.ingridient__nutrition_element}>
          <p className="text text_type_main-default text_color_inactive">
            {type.name}
          </p>
          <p className="text text_type_digits-default text_color_inactive mt-2">
            {type.value}
          </p>
        </div>
      ))}
    </div>
  );
}
