import styles from "./BurgerIngridients.module.css";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import React from "react";
import IngridientsBlock from "../BurgerIngridientBlock/BurgerIngridientBlock";
import Proptypes from "prop-types";

const ingridientsBlockNames = [
  { name: "Булки", value: "bun" },
  {
    name: "Соусы",
    value: "sauce",
  },
  {
    name: "Начинки",
    value: "main",
  },
];

export default function BurgerIngridients({ data }) {
  const [currentBlock, setBlockState] = React.useState("bun");

  React.useEffect(() => {
    document
      .getElementById(currentBlock)
      .scrollIntoView({ behavior: "smooth" });
  }, [currentBlock]);

  return (
    <section className={`mt-30" ${styles.ingridients}`}>
      <h1 className="text text_type_main-large ml-10">Соберите бургер</h1>
      <div className="mt-5 ml-10" style={{ display: "flex" }}>
        {ingridientBlockNames.map((tab) => (
          <Tab
            key={tab.name}
            value={tab.value}
            active={currentBlock === tab.value}
            onClick={setBlockState}
          >
            {tab.name}
          </Tab>
        ))}
      </div>
      <div className={styles.ingridients__blocks}>
        <ul style={{ listStyle: "none" }}>
          {ingridientsBlockNames.map((ingridientName) => (
            <IngridientsBlock
              key={ingridientName.name}
              ingridientBlockName={ingridientName}
              ingridientsArray={data}
            ></IngridientsBlock>
          ))}
        </ul>
      </div>
    </section>
  );
}

BurgerIngridients.propTypes = {
  data: Proptypes.array,
};
