import styles from "./IngridientCard.module.css";
import {
  CurrencyIcon,
  Counter,
} from "@ya.praktikum/react-developer-burger-ui-components";
import React from "react";
import IngredientDetails from "../Modals/IngridientDetails/IngridientDetails";
import Proptypes from "prop-types";

export default function IngredientCard(ingredient) {
  const ingredientElement = ingredient.ingredient;
  const [modal, isModalOpen] = React.useState(false);

  const toggleModal = () => {
    isModalOpen(!modal);
  };

  return (
    <>
      <section onClick={toggleModal} className={styles.ingridient}>
        <Counter count={1} size="default" extraClass="m-1" />
        <img
          src={ingredientElement.image}
          className={styles.ingridient__image}
        />
        <div className={`mt-2 ${styles.ingridient__price}`}>
          <p className="text text_type_digits-default mt-2">
            {ingredientElement.price}
          </p>
          <CurrencyIcon type="primary" />
        </div>
        <p
          className={`text text_type_main-small mt 2 ${styles.ingridient__text}`}
        >
          {ingredientElement.name}
        </p>
      </section>
      {modal && (
        <IngredientDetails
          key={ingredientElement._id}
          ingredient={ingredientElement}
          isModalOpened={modal}
          toggleModal={toggleModal}
        />
      )}
    </>
  );
}

IngredientCard.Proptypes = {
  ingredient: Proptypes.object,
};
