import styles from "./IngridientCard.module.css";
import {
  CurrencyIcon,
  Counter,
} from "@ya.praktikum/react-developer-burger-ui-components";
import React from "react";
import IngridientDetaiils from "../Modals/IngridientDetails/IngridientDetails";
import Proptypes from "prop-types";

export default function IngridientCard(ingridient) {
  const ingridientElement = ingridient.ingridient;
  const [modal, isModalOpen] = React.useState(false);

  const toggleModal = () => {
    isModalOpen(!modal);
  };

  return (
    <>
      <section onClick={toggleModal} className={styles.ingridient}>
        <Counter count={1} size="default" extraClass="m-1" />
        <img
          src={ingridientElement.image}
          className={styles.ingridient__image}
        />
        <div className={`mt-2 ${styles.ingridient__price}`}>
          <p className="text text_type_digits-default mt-2">
            {ingridientElement.price}
          </p>
          <CurrencyIcon type="primary" />
        </div>
        <p
          className={`text text_type_main-small mt 2 ${styles.ingridient__text}`}
        >
          {ingridientElement.name}
        </p>
      </section>
      {modal && (
        <IngridientDetaiils
          key={ingridientElement._id}
          ingridient={ingridientElement}
          isModalOpened={modal}
          toggleModal={toggleModal}
        />
      )}
    </>
  );
}

IngridientCard.Proptypes = {
  ingridient: Proptypes.object,
};
